import {BrowserRouter, Switch, Route} from "react-router-dom";
import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';
import Home from './components/pages/Home';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Header from './components/layout/Header';
import UserContext from "./context/UserContext";

//rendering different components based on the route
//Header should be present in all the pages
export default function App() {
  const [userData, setUserData] = useState({
    token: undefined,
    user: undefined
  })

  useEffect(() => {
    const checkLoggedIn = async () => {
      let token = localStorage.getItem('auth-token');
      if (token == null){
        localStorage.setItem("auth-token", "");
        token = "";
      }
      const tokenRes = await Axios.post(
        "http://localhost:5000/user/tokenIsValid", 
        null, 
        {headers: {"x-auth-token": token}
      });
      if (tokenRes.data){
        const userRes = await Axios.get('http://localhost:5000/user/', {header: {"x-auth-token": token}
      });
      setUserData({
        token, 
        user: userRes.data,
      });
      }
    };
    checkLoggedIn();
  }, []);

  return <>
    <BrowserRouter>
      <UserContext.Provider value={{userData, setUserData}}>
        <Header />
        <Switch>
          <Route exact path = "/" component = {Home}></Route>
          <Route path = '/login' component = {Login}></Route>
          <Route path = '/register' component = {Register}></Route>
        </Switch>
      </UserContext.Provider>.Provider>
    </BrowserRouter>
  </>;
}
